document.getElementById("year").textContent=(new Date).getFullYear();const modal=document.getElementById("project-modal");if(modal){const e=modal.querySelector("img"),t=modal.querySelector("h3"),d=modal.querySelector("p");document.querySelectorAll(".project-item").forEach(l=>{l.addEventListener("click",()=>{e.src=l.querySelector("img").src,e.alt=l.querySelector("img").alt,t.textContent=l.dataset.title||"",d.textContent=l.dataset.description||"",modal.classList.remove("hidden")})}),modal.querySelector(".close-modal").addEventListener("click",()=>{modal.classList.add("hidden")}),modal.addEventListener("click",e=>{"project-modal"===e.target.id&&modal.classList.add("hidden")})}